import React from 'react'
import { Link } from 'react-router-dom';

const Header = () => {
    window.onload = function(){
        const hamburger = document.querySelector(".hamburger");
        const navMenu = document.querySelector(".nav-menu");
        const navLink = document.querySelectorAll(".nav-link");
        hamburger.addEventListener("click", mobileMenu);
        navLink.forEach(n => n.addEventListener("click", closeMenu));

        function mobileMenu() {
            hamburger.classNameList.toggle("active");
            navMenu.classNameList.toggle("active");
        }

        function closeMenu() {
            hamburger.classNameList.remove("active");
            navMenu.classNameList.remove("active");
        }
    }

    return (
        <header id="main-header">
            <nav classNameName="navbar">
                <a href="#" classNameName="nav-logo">
                    <img src="./images/logo-dark.png" alt="" />
                </a>
                <ul classNameName="nav-menu">
                    <li className="nav-item">
                        <Link to classNameName="nav-link">Chat</Link>
                    </li>
                    <li className="nav-item">
                        <Link to > </Link>
                    </li>
                </ul>
                <div className="hamburger">
                    <span className="bar"></span>
                    <span className="bar"></span>
                    <span className="bar"></span>
                </div>
            </nav>
        </header>
    )
}

export default Header
